<?php// Function that creates the createUniverse function (why am I not doing that in PHP?)function defCreateUniverse($name, $sys) {?>    // Generates a new universe    function createUniverse() {        // Create the universe        var uni = new world.Universe(20, 20);                // Create the alon system, home to the alon empire        var alon = new world.StarSystem("<?php echo $sys; ?>", world.pos(9, 2));        alon.add(world.PlanetType.JUNGLE);        alon.add(world.PlanetType.BARREN);        uni.addSystem(alon);        // The omic system        var omic = new world.StarSystem("Omic", world.pos(3, 7));        omic.add(world.PlanetType.WATER);        uni.addSystem(omic);        // The gatnik system, home to the marak federation        var gatnic = new world.StarSystem("Gatnic", world.pos(1, 1));        gatnic.add(world.PlanetType.BARREN);        uni.addSystem(gatnic);        // The rest of the systems        uni.addSystem(new world.StarSystem("Gva", world.pos(5, 3)));        uni.addSystem(new world.StarSystem("Kyky", world.pos(18, 4)));        uni.addSystem(new world.StarSystem("Tado", world.pos(6, 16)));        uni.addSystem(new world.StarSystem("Odot", world.pos(4, 17)));        uni.addSystem(new world.StarSystem("Khiti", world.pos(10, 3)));        uni.addSystem(new world.StarSystem("Mtim", world.pos(4, 11)));        // Create the civilizations        function createCiv(name, type, home) {            var civ = new world.Civ(name, type, home);            home.enter([world.DefaultSpecs.ColonyShip.create(civ),                        world.DefaultSpecs.Scout.create(civ)]);            return civ;        }        var alonians = createCiv("<?php echo $name; ?>", "Empire", alon);        var marakians = createCiv("Marak", "Federation", gatnic);        uni.addCiv(alonians);        uni.addCiv(marakians);                // Return the universe!        return uni;    }<?php}?>